/** 
 * Tests if a function holds true for all elements in an iterable. 
 * Always returns true for an empty iterable. 
 * This function is curried. 
 * @param {Function} f The condition to test for each element.
 * @param {Object} xs The iterable.
 * @returns {boolean}
 */
export const all = (f = x => true) => {
  if (!f) {
    throw new TypeError('f must be a function');
  }
  return xs => {
    for (const x of xs) {
      if (!f(x)) {
        return false;
      }
    }
    return true;
  };
};

/** 
 * Tests if a function holds true for any of the elements in an iterable. 
 * Always returns false for an empty iterable. 
 * This function is curried. 
 * @param {Function} f The condition to test for each element.
 * @param {Object} xs The iterable.
 * @returns {boolean}
 */
export const any = (f = x => true) => {
  if (!f) {
    throw new TypeError('f must be a function');
  }
  return xs => {
    for (const x of xs) {
      if (f(x)) {
        return true;
      }
    }
    return false;
  };
};

/** 
 * Appends an object to the end of an iterable sequence. 
 * This function is curried. 
 * @param {Object} x The object to append.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const append = x => xs => ({
  [Symbol.iterator]: function * () {
    yield * xs;
    yield x;
  }
});

/** 
 * Takes an iterable and returns a new iterable with all of the same elements but with a memory buffer. 
 * This can be used to prevent repeated computations. 
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const buffer = xs => {

  if (!xs[Symbol.iterator]) {
    throw new TypeError('xs must be iterable');
  }

  const b = [];
  const g = xs[Symbol.iterator]();

  const addToBuffer = () => {
    const next = g.next();
    if (next.done) {
      return false;
    }
    b.push(next.value);
    return true;
  };

  return {
    [Symbol.iterator]: function * () {
      let i = 0;
      while (true) {
        while (i >= b.length) {
          const moreToAdd = addToBuffer();
          if (!moreToAdd) {
            return;
          }
        }
        yield b[i];
        i++;
      }
    }
  };
};

/** 
 * Splits an iterable into chunks of a given size. 
 * The last chunk may be less than the specified size. 
 * This function is curried. 
 * @param {Number} n The chunk size.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const chunk = n => {
  if (typeof n !== 'number') {
    throw new TypeError('n must be a number');
  }
  if (n <= 0) {
    throw new RangeError('n must be greater than zero');
  }
  return xs => ({
    [Symbol.iterator]: function * () {
      let ys = [];
      for (const x of xs) {
        ys.push(x);
        if (ys.length == n) {
          yield ys;
          ys = [];
        }
      }
      if (ys.length > 0) {
        yield ys;
      }
    }
  });
};

/** 
 * Concatenates two iterables into one.  
 * This function is curried. 
 * @param {Object} ys The iterable to yield second.
 * @param {Object} xs The iterable to yield first.
 * @returns {Object}
 */
export const concat = ys => xs => ({
  [Symbol.iterator]: function * () {
    yield * xs;
    yield * ys;
  }
});

/** 
 * Counts the number of elements in an iterable. 
 * @param {Object} xs The iterable.
 * @returns {number}
 */
export const count = xs => {
  let i = 0;
  for (const _ of xs) {
    i++;
  }
  return i;
};

export const defaultComparison = (i, j) => {
  if (i == j) {
    return 0;
  }
  if (i > j) {
    return 1;
  }
  return -1;
};

export const defaultEquality = {
  hashCode: _ => 0, 
  isEqual: (x, y) => x == y,
};

/** 
 * An empty iterable. 
 */
export const empty = {
  [Symbol.iterator]: function * () {}
};

/** 
 * Takes an iterable and returns a new one including only elements that satisfy a given predicate.  
 * The order of the iterable is maintained. 
 * This function is curried. 
 * @param {Object} f The predicate.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const filter = f => {
  if (!f) {
    throw new TypeError('f must be a function');
  }
  return xs => ({
    [Symbol.iterator]: function * () {
      for (const x of xs) {
        if (f(x)) {
          yield x;
        }
      }
    }
  });
};

/** 
 * Returns the first element of an iterable that satisfies a given predicate.   
 * This function will throw an error if no satisfactory element is found.  
 * This function is curried. 
 * @param {Object=} f The predicate, defaults to always true.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const first = (f = _ => true) => {
  if (!f) {
    throw new TypeError('f must be a function');
  }
  return xs => {
    for (const x of xs) {
      if (f(x)) {
        return x;
      }
    }
    throw new Error('Expected at least one element');
  };
};

/** 
 * Takes an iterable and returns a new one where each element is transformed using the given function.  
 * Unlike map, the function is expected to return an iterable for each element. These iterables are concatenated. 
 * The order of the iterable is maintained. 
 * This function is curried. 
 * @param {Object} f The transformation function that returns an iterable.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const flatMap = f => {
  if (!f) {
    throw new TypeError('f must be a function');
  }
  return xs => ({
    [Symbol.iterator]: function * () {
      for (const x of xs) {
        yield * f(x);
      }
    }
  });
};

/** 
 * Given an iterable returns a new iterable that repeats it forever. 
 * Take care, since the resulting iterable is infinite. 
 * @param {Object} xs The iterable to repeat.
 * @returns {Object}
 */
export const forever = xs => {
  if (!xs) {
    throw new TypeError('xs must be defined');
  }
  return {
    [Symbol.iterator]: function * () {
      while (true) {
        yield * xs; 
      }
    }
  };
};

/** 
 * Given an array, returns an iterable with equivalent elements. 
 * You should not mutate the array used to create the iterable. 
 * @param {Object} xs The array.
 * @returns {Object}
 */
export const fromArray = xs => {
  if (!Array.isArray(xs)) {
    throw new TypeError('xs must be an array');
  }
  return {
    [Symbol.iterator]: function * () {
      yield * xs;
    }
  };
};

/** 
 * Given a generator function, returns an iterable with equivalent elements. 
 * @param {Object} xs The generator function.
 * @returns {Object}
 */
export const fromGenerator = f => {
  if (!f) {
    throw new TypeError('f must be a generator function');
  }
  return {
    [Symbol.iterator]: function * () {
      yield * f();
    }
  };
};

/** 
 * Returns the first element of an iterable. 
 * If the iterable is empty then an error is thrown. 
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const head = xs => {
  for (const x of xs) {
    return x;
  }
  throw new Error('Expected at least one element');
};

import { defaultEquality } from './default-equality.js';

/** 
 * Tests if a an iterable contains a given element. 
 * Also takes an equality object as an optional argument. 
 * This function is partially curried. 
 * @param {Object} element The element to look for. 
 * @param {Object} [equality=defaultEquality] The equality system to use. 
 * @param {Object} xs The iterable to search. 
 * @returns {boolean}
 */
export const includes = (element, equality = defaultEquality) => {
  const { isEqual } = equality;
  if (!isEqual) {
    throw new TypeError('equality must have a isEqual property');
  }
  return xs => {
    for (const x of xs) {
      if (isEqual(element, x)) {
        return true;
      }
    }
    return false;
  };
};

