/** 
 * Tests if a given object is an iterable 
 * @param {Object} xs The object to test.
 * @returns {Boolean}
 */
export const isIterable = xs => !!xs && !!xs[Symbol.iterator];

/** 
 * Joins all elements in an iterable using the given seperator. 
 * If the iterable is empty then the empty string is returned. 
 * @param {String=} s The seperator string. 
 * @param {Object} xs The iterable to join. 
 * @returns {String}
 */
export const join = (s = '') => xs => {
  if (!xs || !xs[Symbol.iterator]) {
    throw new TypeError('xs must be iterable');
  }
  let result = '';
  let isFirst = true;
  for (const x of xs) {
    if (isFirst) {
      result = x;
      isFirst = false;
    } else {
      result = result + s + x;
    }
  }
  return result;
};

/** 
 * Constructs an iterable from a fixed list of elements. 
 * This function is variadic. 
 * @param {...Object} elements The elements of the iterable. 
 * @returns {Object}
 */
export const just = function() {
  const xs = [ ...arguments ];
  return ({
    [Symbol.iterator]: function * () {
      for (const x of xs) {
        yield x;
      }
    }
  });
};

/** 
 * Returns the last element of an iterable that satisfies a given predicate.   
 * This function will throw an error if no satisfactory element is found.  
 * This function is curried. 
 * @param {Object=} f The predicate, defaults to always true.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const last = (f = _ => true) => xs => {
  let hasFoundValue = false;
  let y = null;
  for (const x of xs) {
    if (f(x)) {
      hasFoundValue = true;
      y = x;
    }
  }
  if (hasFoundValue) {
    return y;
  }
  throw new Error('Expected at least one element');
};

/** 
 * Takes an iterable and returns a new one where each element is transformed using the given function.  
 * The order of the iterable is maintained. 
 * This function is curried. 
 * @param {Object} f The transformation function.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const map = f => {
  if (!f) {
    throw new TypeError('f must be a function');
  }
  return xs => ({
    [Symbol.iterator]: function * () {
      for (const x of xs) {
        yield f(x);
      };
    }
  });
};

