/** 
 * Takes an iterable and returns the largest element, according to the given comparison. 
 * This function is curried. 
 * @param {Object=} c The comparison.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const max = (c = defaultComparison) => {
  if (!c) {
    throw new TypeError('c must be defined');
  }
  return min((i, j) => c(j, i));
};

import { defaultComparison } from './default-comparison.js';

/** 
 * Takes an iterable and returns the smallest element, according to the given comparison. 
 * This function is curried. 
 * @param {Object=} c The comparison.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const min = (c = defaultComparison) => {
  if (!c) {
    throw new TypeError('c must be defined');
  }
  return xs => {
    if (!xs) {
      throw new TypeError('xs must be define');
    }
    let min = null;
    let foundMin = false;
    for (const x of xs) {
      if (foundMin) {
        if (c(x, min) < 0) {
          min = x;
        }
      } else {
        foundMin = true;
        min = x;
      }
    }
    if (foundMin) {
      return min;
    }
    throw new Error('Expected at least one element but found none');
  };
};

/** 
 * Tests if a function holds true for none of the elements in an iterable. 
 * Always returns true for an empty iterable. 
 * By default, this function returns false for an iterable with any elements. 
 * This function is curried. 
 * @param {Function=} f The condition to test for each element.
 * @param {Object} xs The iterable.
 * @returns {boolean}
 */
export const none = (f = _ => true) => {
  if (!f) {
    throw new TypeError('f must be a function');
  }
  return xs => {
    for (const x of xs) {
      if (f(x)) {
        return false;
      }
    }
    return true;
  };
};

/** 
 * Returns the nth element of an iterable.    
 * This function will throw an error if the iterable is not long enough.  
 * This function is curried. 
 * @param {Number} n The zero-based index of the element to return.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const nth = n => {
  if (!Number.isSafeInteger(n)) {
    throw new TypeError('n must be an integer');
  }
  if (n < 0) {
    throw new RangeError('n must be at least zero');
  }
  return xs => {
    let i = 0;
    for (const x of xs) {
      if (i === n) {
        return x;
      }
      i++;
    }
    throw new Error('Tried to take the ' + n + 'th element, but there were only ' + i);
  };
};

/** 
 * Takes an iterable and returns a new iterable that may only be iterated once. 
 * This function is useful for debugging purposes. 
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const once = xs => {
  if (!xs) {
    throw new TypeError('xs must be defined');
  }
  let hasBeenCalled = false;
  return {
    [Symbol.iterator]: function * () {
      if (hasBeenCalled) {
        throw new Error('once(xs) may only be iterated once. ');
      } else {
        hasBeenCalled = true;
      }
      yield * xs;
    }
  };
};

/** 
 * Prepends an object to the start of an iterable sequence. 
 * This function is curried. 
 * @param {Object} x The object to prepend.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const prepend = x => xs => ({
  [Symbol.iterator]: function * () {
    yield x;
    yield * xs;
  }
});

/** 
 * Creates an iterable of numbers from zero to the given limit. 
 * @param {Number=} n The number to count to.
 * @returns {Object}
 */
export const range = (n = Infinity) => {
  if (typeof n !== 'number') {
    throw new TypeError('n must be a number');
  }
  if (n < 0) {
    throw new RangeError('n must be at least zero');
  }
  return {
    [Symbol.iterator]: function * () {
      for (let i = 0; i < n; i++) {
        yield i;
      }
    }
  };
};

/** 
 * Performs a reduction of an iterable, starting from a given state and reducer function. 
 * Note that this function must not be called on an infinite iterable. 
 * This function is partially curried. 
 * @param {Object} s The initial state. 
 * @param {Object} f The reducer function. 
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const reduce = (s, f) => (xs) => {
  let w = s;
  for (const x of xs) {
    w = f(w, x);
  }
  return w;
};

/** 
 * Takes an iterable and returns a new iterable where
 * the nth element has been skipped.  
 * This function is curried. 
 * @param {Number} n The index of the element to replace. 
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const remove = n => xs => ({
  [Symbol.iterator]: function * () {
    let i = 0;
    for (const x of xs) {
      if (i != n) {
        yield x;
      }
      i++;
    }
  }
});

/** 
 * Repeats an iterable for the given number of times. 
 * This function is curried. 
 * @param {Number} n The number of repetitions. 
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const repeat = n => {
  if (Number.isNaN(n) || !Number.isSafeInteger(n)) {
    throw new TypeError('n must be a number');
  }
  if (n < 0) {
    throw new RangeError('n must be at least zero');
  }
  return xs => ({
    [Symbol.iterator]: function * () {
      for (let i = 0; i < n; i++) {
        yield * xs;
      }
    }
  });
};

/** 
 * Given an iterable, returns a new iterable with the elements reversed. 
 * Note that this function must not be called on an infinite iterable. 
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const reverse = xs => ({
  [Symbol.iterator]: function * () {
    const ys = [ ...xs ];
    ys.reverse();
    yield * ys;
  }
});

/** 
 * Takes an iterable and returns a new iterable where
 * the nth element has been set to a given value.  
 * This function is curried. 
 * @param {Number} n The index of the element to replace. 
 * @param {Object} value The new value. 
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const set = (n, value) => xs => ({
  [Symbol.iterator]: function * () {
    let i = 0;
    for (const x of xs) {
      if (i == n) {
        yield value;
      } else {
        yield x;
      }
      i++;
    }
  }
});

/** 
 * Returns the first element of an iterable that satisfies a given predicate.   
 * This function will throw an error if no satisfactory element is found.  
 * This function is curried. 
 * @param {Object=} f The predicate, defaults to always true.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const single = (f = _ => true) => xs => {
  let hasFoundValue = false;
  let y = null;
  for (const x of xs) {
    if (f(x)) {
      if (hasFoundValue) {
        throw new Error('Expected exactly one element but found at least two');
      }
      hasFoundValue = true;
      y = x;
    }
  }
  if (hasFoundValue) {  
    return y;
  }
  throw new Error('Expected exactly one element but found none');
};

/** 
 * Skips elements from an iterable for as long as the elements satisfy a given condition. 
 * The first element that satisfies the condition will be included. 
 * This function is curried. 
 * @param {Object} f The function.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const skipUntil = f => {
  if (!f) {
    throw new TypeError('f must be a function');
  }
  return xs => ({
    [Symbol.iterator]: function * () {
      let shouldYield = false;
      for (const x of xs) {
        if (shouldYield) {
          yield x;
        } else {
          if (f(x)) {
            yield x;
            shouldYield = true;
          }
        }
      }
    }
  });
};

/** 
 * Skips the first n elements of an iterable. 
 * If there are fewer than n elements in an iterable, then an empty iterable will be returned. 
 * This function is curried. 
 * @param {Number} n The number of elements to skip.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const skip = n => {
  if (!Number.isSafeInteger(n)) {
    throw new TypeError('n must be an integer');
  }
  if (n < 0) {
    throw new RangeError('n must be at least zero');
  }
  return xs => ({
    [Symbol.iterator]: function * () {
      let i = 0;
      for (const x of xs) {
        if (i >= n) {
          yield x;
        }
        i++;
      }
    }
  });
};

import { isIterable } from './is-iterable.js';

const peek = xs => xs[xs.length - 1];

/** 
 * Deep flattens an iterable and all of its nested iterables. 
 * Strings are not considered iterables for this function. 
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const smoosh = xs => ({
  [Symbol.iterator]: function * () {
    const pending = [ xs[Symbol.iterator]() ];
    while (pending.length > 0) {
      let { value, done } = peek(pending).next();
      if (done) {
        pending.pop();
      } else {
        if (typeof value !== 'string' && isIterable(value)) {
          pending.push(value[Symbol.iterator]());
        } else {
          yield value;
        }
      }
    }
  }
});

import { defaultComparison } from './default-comparison.js';
import { map } from './map.js';
import { toArray } from './to-array.js';

export const sortedByDesc = (f, c = defaultComparison) => {
  if (!f) {
    throw new TypeError('f must be defined');
  }
  if (!c) {
    throw new TypeError('c must be defined');
  }
  const reversedComparison = (i, j) => c(j.score, i.score);
  return xs => ({
    [Symbol.iterator]: function * () {
      const ys = [ ...map(x => ({ score: f(x), value: x }))(xs) ];
      ys.sort(reversedComparison);
      yield * map(x => x.value)(ys);
    }
  });
};

import { defaultComparison } from './default-comparison.js';
import { map } from './map.js';
import { toArray } from './to-array.js';

export const sortedBy = (f, c = defaultComparison) => {
  if (!f) {
    throw new TypeError('f must be defined');
  }
  if (!c) {
    throw new TypeError('c must be defined');
  }
  const comparison = (i, j) => c(i.score, j.score);
  return xs => ({
    [Symbol.iterator]: function * () {
      const ys = [ ...map(x => ({ score: f(x), value: x }))(xs) ];
      ys.sort(comparison);
      yield * map(x => x.value)(ys);
    }
  });
};

