  isEqual: (x, y) => x === y,
};

const hashCode = (x) => {
  const s = JSON.stringify(x);

  let hash = 0;
  let chr = null;

  if (s.length === 0) {
    return hash;
  }

  for (let i = 0; i < s.length; i++) {
    chr   = s.charCodeAt(i);
    hash  = ((hash << 5) - hash) + chr;
    hash |= 0; // Convert to 32bit integer
  }

  return hash;
};

export const stringifyEquality = {
  hashCode,
  isEqual: (x, y) => JSON.stringify(x) === JSON.stringify(y),
};

const hashCodeString = s => {
  let h = 0;

  for (let i = 0; i < s.length; i++) {
    h = (h << 5) - h + s.charCodeAt(i) | 0;
  }

  return h;
};

const hashCodeNumber = x => {
  if (Number.isNaN(x)) {
    return 0;
  }

  if (!Number.isFinite(x)) {
    return 0;
  }

  return Math.trunc(x) % 2147483647; // A large prime number
};

const hashCodeObject = (x, visited) => {
  let hash = 0;

  for (const [ key, value ] of Object.entries(x)) {
    hash = ((hash << 5) - hash) +
      hashCodeString(key) * 7 +
      hashCode(value, visited);
    hash = hash & hash;
  }

  return hash;
};

export function hashCode(x, visited = new Set()) {
  if (x === undefined) {
    throw new ReferenceError('x must not be undefined');
  }

  if (x === null) {
    return 0;
  }

  if (typeof x === 'number') {
    return hashCodeNumber(x);
  }

  if (typeof x === 'string') {
    return hashCodeString(x);
  }

  if (typeof x === 'object') {
    if (visited.has(x)) {
      return 0;
    }

    visited.add(x);

    return hashCodeObject(x, visited);
  }

  return 0;
};

import { hashCode } from './hash-code.js';
import { isEqual } from './is-equal.js';

export const structuralEquality = {
  hashCode,
  isEqual,
};

export const isEqual = (x, y) => {
  if (x === undefined) {
    throw new ReferenceError('x must not be undefined');
  }

  if (y === undefined) {
    throw new ReferenceError('y must not be undefined');
  }

  if (x === y) {
    return true;
  }

  if (x === null) {
    return y === null;
  }

  if (typeof x === 'number') {
    return x === y;
  }

  if (typeof x === 'string') {
    return x === y;
  }

  if (typeof x === 'object') {
    if (typeof y !== 'object') {
      return false;
    }

    if (Object.entries(x).length !== Object.entries(y).length) {
      return false;
    }

    for (const [ key, value ] of Object.entries(x)) {
      if (y[key] === undefined) {
        return false;
      }

      if (!isEqual(value, y[key])) {
        return false;
      }
    }

    return true;
  }

  return false;
};

/** 
 * Sums all elements in an iterable. 
 * If the iterable is empty then zero is returned. 
 * @param {Object} xs The iterable.
 * @returns {Number}
 */
export const sum = xs => {
  if (!xs || !xs[Symbol.iterator]) {
    throw new TypeError('xs must be iterable');
  }
  let result = 0;
  for (const x of xs) {
    result = result + x;
  }
  return result;
};

/** 
 * Returns an iterable of all elements except the first. 
 * If the iterable is empty then an error is thrown. 
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const tail = xs => ({
  [Symbol.iterator]: function * () {
    let isFirst = true;
    for (const x of xs) {
      if (isFirst) {
        isFirst = false;
      } else {
        yield x;
      }
    }
    if (isFirst) {
      throw new Error('Expected at least one element');
    }
  }
});

/** 
 * Takes elements from an iterable for as long as the elements satisfy a given condition. 
 * The first element that does satisfies the condition will not be included. 
 * This function is curried. 
 * @param {Object} f The function.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const takeWhile = f => {
  if (!f) {
    throw new TypeError('f must be a function');
  }
  return xs => ({
    [Symbol.iterator]: function * () {
      for (const x of xs) {
        if (!f(x)) {
          return;
        }
        yield x;
      }
    }
  });
};

/** 
 * Takes the first n elements of an iterable. 
 * If there are fewer than n elements in an iterable, then this has no effect. 
 * This function is curried. 
 * @param {Number} n The number of elements to take.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const take = n => {
  if (!Number.isSafeInteger(n)) {
    throw new TypeError('n must be an integer');
  }
  if (n < 0) {
    throw new RangeError('n must be at least zero');
  }
  return xs => ({
    [Symbol.iterator]: function * () {
      let i = 0;
      for (const x of xs) {
        if (i >= n) {
          return;
        }
        yield x;
        i++;
      }
    }
  });
};

/** 
 * Takes an iterable and converts it into an array. 
 * This should only be called on finite iterables. 
 * @param {Object} xs The iterable.
 * @returns {Array}
 */
export const toArray = xs => {
  if (Array.isArray(xs)) {
    return xs;
  }
  const result = [];
  for (const x of xs) {
    result.push(x);
  }
  return result;
};

/** 
 * Takes an iterable and converts it into a set. 
 * This should only be called on finite iterables. 
 * @param {Object} xs The iterable.
 * @returns {Set}
 */
export const toSet = xs => {
  return new Set(xs);
};

import { defaultEquality } from './default-equality.js';

/** 
 * Takes an iterable and returns a new one with duplicate elements removed.  
 * The order of the iterable is maintained. 
 * This function is curried. 
 * @param {Object=} e The equality system to use.
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const unique = (e = defaultEquality) => {
  const { hashCode, isEqual } = e;
  return xs => ({
    [Symbol.iterator]: function * () {
      const buckets = new Map();
      for (const x of xs) {
        const h = hashCode(x);
        const bucket = buckets.get(h);
        if (bucket) {
          let foundExactMatch = false;
          for (const y of bucket) {
            if (isEqual(x, y)) {
              foundExactMatch = true;
              break;
            }
          }
          if (!foundExactMatch) {
            yield x;
            bucket.push(x);
          }
        } else {
          yield x;
          buckets.set(h, [ x ]);
        }
      }
    }
  });
};

/** 
 * Takes an iterable and returns a new iterable where
 * the nth element has been updated with the given function.  
 * This function is curried. 
 * @param {Number} n The index of the element to replace. 
 * @param {Function} f The update function. 
 * @param {Object} xs The iterable.
 * @returns {Object}
 */
export const update = (n, f) => xs => ({
  [Symbol.iterator]: function * () {
    let i = 0;
    for (const x of xs) {
      if (i == n) {
        yield f(x);
      } else {
        yield x;
      }
      i++;
    }
  }
});

/** 
 * Combines two iterables into one by pairing elements. 
 * Each pair is a two element array. 
 * The longer of the two iterables will be truncated to match the length of the shorter one.  
 * This function is curried. 
 * @param {Object} ys The iterable whose elements will be the second element of the pairs.
 * @param {Object} xs The iterable whose elements will be the first element of the pairs.
 * @returns {Object}
 */
export const zip = ys => {
  if (!ys[Symbol.iterator]) {
    throw new TypeError('ys must be iterable');
  }
  return xs => {
    if (!xs[Symbol.iterator]) {
      throw new TypeError('xs must be iterable');
    }
    return {
      [Symbol.iterator]: function * () {
        const xsGenerator = xs[Symbol.iterator]();
        const ysGenerator = ys[Symbol.iterator]();
        while (true) {
          const x = xsGenerator.next();
          const y = ysGenerator.next();
          if (x.done || y.done) {
            break;
          }
          yield [ x.value, y.value ];
        }
      }
    };
  };
};
